import React from "react";
import logo from "./logo.svg";
import "./App.css";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";

function App() {
  const data = [
    {
      title: "Frisbee on the Lawn",
      description: "Play frisbee with first-years.",
    },
    {
      title: "20% Off Bodos!",
      description: "Meet at Bodos with a friend and get a 20% off coupon!",
    },
    {
      title: "Lunch with Alumni",
      description:
        "Great networking opportunity to have lunch with a UVA alumni.",
    },
    {
      title: "Party @ Trin 3",
      description:
        "Just be sure to wear a mask and stay 6ft apart! (also maybe just don't go)",
    },
  ];

  function markovMe() {
    const markovChain = {};
    const textArr = document.getElementById("inputBox").value.split(" ");
    for (let i = 0; i < textArr.length; i++) {
      let word = textArr[i].toLowerCase().replace(/[\W_]/, "");
      if (!markovChain[word]) {
        markovChain[word] = [];
      }
      if (textArr[i + 1]) {
        markovChain[word].push(
          textArr[i + 1].toLowerCase().replace(/[\W_]/, "")
        );
      }
    }
    const words = Object.keys(markovChain);
    let word = words[Math.floor(Math.random() * words.length)];
    let result = "";
    for (let i = 0; i < words.length; i++) {
      result += word + " ";
      var newWord =
        markovChain[word][Math.floor(Math.random() * markovChain[word].length)];
      word = newWord;
      if (!word || !markovChain.hasOwnProperty(word))
        word = words[Math.floor(Math.random() * words.length)];
    }
    document.getElementById("markovResults").innerText = result;
  }

  return (
    <div className="App">
      <AppBar position="static" style={{ height: 75, marginBottom: 75 }}>
        <Toolbar>
          <Typography variant="h6">Gainz.io</Typography>
        </Toolbar>
      </AppBar>
      <h1 align="center">Markov Me!</h1>
      <div display="inline-block">
        <div>
          <h2>
            Input text here!{" "}
            <button onClick={() => markovMe()}>Click me to generate text!</button>
          </h2>
          <textarea id="inputBox" rows="10" cols="100"></textarea>
        </div>
        <div>
          <p id="markovResults" />
        </div>
      </div>
    </div>
  );
}

export default App;
